class Durations:
    """
    a convenience class of note durations
    """
    onehundredtwentyeighth = 1 / 128
    onehundredtwentyeighth_triplet = onehundredtwentyeighth * 2 / 3
    sixtyfourth = 1 / 64
    sixtyfourth_triplet = sixtyfourth * 2 / 3
    thirtysecond = 1 / 32
    thirtysecond_triplet = thirtysecond * 2 / 3
    sixteenth = 1 / 16
    sixteenth_triplet = sixteenth * 2 / 3
    eighth = 1 / 8
    eighth_triplet = eighth * 2 / 3
    quarter = 1 / 4
    quarter_triplet = quarter * 2 / 3
    half = 1 / 2
    half_triplet = half * 2 / 3
    whole = 1
    whole_triplet = whole * 2 / 3
    doublewhole = 2
    doublewhole_triplet = doublewhole * 2 / 3
