

class ChangeContextRequest(object):

    def __init__(self):
        self.action = None
        self.contextName = None

    def getAction(self):
        return self.action

    def setAction(self, action):
        self.action = action

    def getContextName(self):
        return self.contextName

    def setContextName(self, contextName):
        self.contextName = contextName
